generated.css:
* {
  margin: 0;
  padding: 0;
  font-size: 15px;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
  color: #000;
}

header {
  width: 100%;
  height: 80px;
  background-color: #0D4795;
  opacity: 0.9;
  box-sizing: border-box;
  position: fixed;
}
header .auto {
  max-width: 1200px;
  margin: 0 auto;
  min-width: 980px;
}
header .auto .logo-wrap {
  padding: 0 30px;
  box-sizing: border-box;
  width: 250px;
  height: 80px;
  line-height: 80px;
  float: left;
  margin: 0 auto;
}
header .auto .nav-wrap {
  min-width: 730px;
  display: block;
  margin: 0 auto;
  float: right;
}
header .auto .nav-wrap > ul > li {
  float: left;
  position: relative;
}
header .auto .nav-wrap ul li a {
  position: relative;
  padding: 0 20px;
  display: inline-block;
  background-color: #0D4795;
  color: #fff;
  font-size: 1em;
  font-weight: bold;
  height: 80px;
  line-height: 80px;
  z-index: 2;
}
header .auto .nav-wrap ul li a i {
  padding-left: 5px;
}
header .auto .nav-wrap ul li .sub-menu {
  background-color: #0D4795;
  display: inline-block;
  position: absolute;
  border-radius: 3px;
  transition: all 0.25s ease-out;
  transform: translateY(-100%);
  z-index: 1;
}
header .auto .nav-wrap ul li .sub-menu ul li {
  width: 115px;
  text-align: center;
}
header .auto .nav-wrap ul li .sub-menu ul li a {
  padding: 0 20px;
  display: inline-block;
  color: #fff;
  font-size: 1em;
  font-weight: bold;
  height: 40px;
  line-height: 40px;
  font-size: 0.8em;
}
header .auto .nav-wrap ul li .sub-menu ul li a:hover {
  background: rgba(50, 50, 50, 0.97);
}
header .auto .nav-wrap ul li .sub-menu ul li:hover {
  background: rgba(50, 50, 50, 0.97);
}
header .auto .nav-wrap ul li:hover .sub-menu {
  transform: translateY(0);
}
header .auto .nav-wrap ul .link-wrap {
  display: inline-block;
  position: relative;
  line-height: 80px;
  height: 80px;
}
header .auto .nav-wrap ul .link-wrap .select-wrap {
  display: inline-block;
  margin-right: 5px;
}
header .auto .nav-wrap ul .link-wrap .select-wrap select {
  color: #fff;
  border-radius: 3px;
  background-color: transparent;
  padding: 5px;
  border: 1.5px solid #fff;
  width: 70px;
  font-size: 0.8em;
}
header .auto .nav-wrap ul .link-wrap .btn-blog {
  display: inline;
}
header .auto .nav-wrap ul .link-wrap .btn-blog img {
  vertical-align: middle;
}

.main-visual-wrap {
  display: none;
}
.main-contents {
  display: none;
}
.footer {
  display: none;
}
