}

ul {
  list-style-type: none;
  padding: 0;
  margin: 0;
}

li, a {
  cursor: pointer;
}

a {
  text-decoration: none;
}

a, span, i {
  color: #fff;
  font-size: 19px;
}

ul li {
  position: absolute;
}

